mapper.select:
SELECT cf.*, p.*, i.* from (select c.*, pf.fileName from cart c inner join productfiles pf on c.productNum = pf.productNum where
(c.username=#{item.username} and c.cartNum=#{item.cartNum} and pf.fileName like concat('fileName', '_'))
) cf INNER JOIN product p ON cf.productNum=p.productNum INNER JOIN productinfo i on cf.pInfoNum=i.pInfoNum
