mapper.select:
select * from (select row_number() over( order by
productTitle asc
productPrice asc
productPrice desc
productNum desc
) as R, P.* from (select * from product where productSaleable = 1 and collab like concat('%',#{collab},'%') and productType like concat('%',#{productType},'%')
and productDisRate > 0
) as P ) as RP where R between #{startRow} and #{lastRow}
